import React, {useState, useEffect} from 'react' 
import { Chat, addResponseMessage } from 'react-chat-popup';
import { Modal} from 'react-bootstrap';
import andre from '../components/assets/img/andre.png'

const Chats = (props) => {
    const {name} = props
    const [chat, isChat] = useState ({
        chatContent: ''
    });
    const handleChange = (e) => {
        const { name, value } = e.target;
        isChat({
            ...chat,
            [name]: value,
        });
    };
    console.log(chat)
    const { chatContent } = chat;
    const handleSubmit = async (e) => {
        e.preventDevault();
        localStorage.setItem('chatting' ,{chat})
    }
    return (
        <Modal {...props}>
            <div className='chatBox'>
                <h4>Talk to {name}</h4>
                <div>
                <Chat 
                profileAvatar={name}
                title={`Talk to ${name}`}
                subtitle="And my cool subtitle" />
                </div>
                <form className='chatForm' onSubmit={handleSubmit} >
                    <input value={chatContent} name='chatContent' onChange={handleChange} type='text' placeholder='type something...' />
                    <button type='submit'>send</button>
                </form>
            </div>
        </Modal>
    )
}

export default Chats
