import React, {useState, useEffect} from 'react' 
import { Chat, addResponseMessage } from 'react-chat-popup';
import { Modal, Col, Row } from 'react-bootstrap';
import andre from '../components/assets/img/andre.png'

const Chats = (props) => {
    const {name} = props
    const personChat = [
        {'name': 'Andre', 'chat': 'Hi! How are you?' },
        {'name': 'Chandra', 'chat': 'Have you read the lattest news?'}, 
        {'name': 'Enteng', 'chat': 'Please send me the Document'}, 
        {'name': 'Richard', 'chat': 'Hello, Good Day Sir! How are you?'},
        {'name': 'deviana', 'chat': 'Helloo'}]
    const [chat, isChat] = useState ({
        chatContent: ''
    });
    const chatDetail = personChat.name.find(personChat => personChat.name === props.match.params.name)
    const handleChange = (e) => {
        const { name, value } = e.target;
        isChat({
            ...chat,
            [name]: value,
        });
    };
    console.log(chat)
    const { chatContent } = chat;
    const handleSubmit = async (e) => {
        e.preventDevault();
        localStorage.setItem('chatting', chatContent)
    }
    return (
        <Modal {...props}>
            <div className='chatBox'>
                <h4>Talk to {name}</h4>
                <div>
                    <Col>
                        <Row>

                        </Row>
                    </Col>
                </div>
                <form className='chatForm' onSubmit={handleSubmit} >
                    <input value={chatContent} name='chatContent' onChange={handleChange} type='text' placeholder='type something...' />
                    <button type='submit' className='sendButton'>send</button>
                </form>
            </div>
        </Modal>
    )
}

export default Chats
