import React, {useState, useEffect} from 'react' 
import { Chat, addResponseMessage } from 'react-chat-popup';
import { Modal, Col, Row } from 'react-bootstrap';
import andre from '../components/assets/img/andre.png'

const Chats = (props) => {
    const {name} = props
    const date = new Date();
    const min = date.getMinutes();
    if(min < 10){
        min = 0+min
    }
    const hour = date.getHours();
    const waktu = hour+':'+min
    const personChat = [
        {'name': 'Andre', 'chat': 'Hi! How are you?' },
        {'name': 'Chandra', 'chat': 'Have you read the lattest news?'}, 
        {'name': 'Enteng', 'chat': 'Please send me the Document'}, 
        {'name': 'Richard', 'chat': 'Hello, Good Day Sir! How are you?'},
        {'name': 'deviana', 'chat': 'Helloo'}]
    
    const chatDetail = personChat.find(({name}) => name === props)
    console.log(chatDetail, 'hasil pencarian')
        const [chat, isChat] = useState ({
        chatContent: []
    });
   
    const handleChange = (e) => {
        const { name, value } = e.target;
        isChat({
            ...chat,
            [name]: value,
        });
    };
    const { chatContent } = chat;
    const handleSubmit = (e) => {
        localStorage.setItem(`${name} chatting`, chatContent)
        localStorage.setItem(`${name} chatTime`, waktu)
        
    }

    
    console.log(waktu, 'menit sekarang')
    return (
        <Modal {...props}>
            <div className='chatBox'>
                <h4>Talk to {name}</h4>
                
                <div>
                    <Col>
                        <Row>

                        </Row>
                    </Col>
                </div>
                <form className='chatForm' >
                    <input value={chatContent} name='chatContent' onChange={handleChange} type='text' onKeyPress={e => {
                        if (e.key === 'Enter') e.preventDefault();
                    }} placeholder='type something...' />
                    <button type='button' onClick={handleSubmit} className='sendButton'>send</button>
                </form>
            </div>
        </Modal>
    )
}

export default Chats
