import React, {Component} from 'react'
import * as am4core from "@amcharts/amcharts4/core";
import * as am4maps from "@amcharts/amcharts4/maps";
import am4themes_animated from "@amcharts/amcharts4/themes/animated"; 
import Modals from '../components/modalSignup'
import '../App.css'

import { Button, Col, Row, Container, Form } from 'react-bootstrap'
am4core.useTheme(am4themes_animated);

// const Register = () => {
    class Maps extends Component {

        state = {
            isOpen: false
          };

          openModal = () => this.setState({ isOpen: true });
          closeModal = () => this.setState({ isOpen: false });

        componentDidMount() {
          // Create map instance
          var chart = am4core.create("chartdiv", am4maps.MapChart);

          // Set map definition
          chart.geodataSource.url = "https://www.amcharts.com/lib/4/geodata/json/indonesiaLow.json";
          chart.geodataSource.events.on("parseended", function(event) {
            let data = [];
            for(var i = 0; i < event.target.data.features.length; i++) {
              data.push({
                id: event.target.data.features[i].id,
                value: Math.round( Math.random() * 10000 )
              })
            }
            polygonSeries.data = data;
          })

          // Set projection
          chart.projection = new am4maps.projections.Mercator();

          // Add zoom control
          chart.zoomControl = new am4maps.ZoomControl();

          // Set initial zoom
          chart.homeZoomLevel = 0.5;

          // Create map polygon series
          var polygonSeries = chart.series.push(new am4maps.MapPolygonSeries());
          polygonSeries.mapPolygons.template.strokeWidth = 0.5;
        //   polygonSeries.exclude = ["AQ"];

          // Make map load polygon (like country names) data from GeoJSON
          polygonSeries.useGeodata = true;

          // Configure series
          var polygonTemplate = polygonSeries.mapPolygons.template;

          polygonTemplate.tooltipText = "{name}: {value}";
          polygonTemplate.fill = am4core.color("#aac4e7");

          // Create hover state and set alternative fill color
          var hs = polygonTemplate.states.create("hover");
          hs.properties.fill = chart.colors.getIndex(0);
          polygonSeries.data = [{
            "id": "ID-SA",
            "name": "Sulawesi Utara",
            "value": 304,
            "fill": am4core.color("#F05C5C")
          }];

          polygonTemplate.propertyFields.fill = "fill";

          // Create active state
          var activeState = polygonTemplate.states.create("active");
          activeState.properties.fill = chart.colors.getIndex(1);

          // Create an event to toggle "active" state
          polygonTemplate.events.on("hit", function(ev) {
            ev.target.isActive = !ev.target.isActive;
            chart.openPopup("We clicked on <strong>" + ev.target.dataItem.dataContext.name + "</strong>" + ev.target.dataItem.dataContext.value);

          });

          let button = chart.chartContainer.createChild(am4core.Button);
            button.padding(5, 5, 5, 5);
            button.align = "right";
            button.marginRight = 15;
            button.events.on("hit", function() {
            chart.goHome();
            });
            button.icon = new am4core.Sprite();
            button.icon.path = "M16,8 L14,8 L14,16 L10,16 L10,10 L6,10 L6,16 L2,16 L2,8 L0,8 L8,0 L16,8 Z M16,8";

        }

        componentWillUnmount() {
          if (this.chart) {
            this.chart.dispose();
          }
        }


        render() {
        // const [modalShow, setModalShow] = useState(false); onClick={this.openModal}

          return (
        <div>
            <div id="chartdiv" style={{ width: "100%", height: "700px" }}>

            </div>
            <Modals show={this.state.isOpen} onHide={this.closeModal} />

        </div>
    )
}}

export default Maps
